/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `after_payment_insert` AFTER INSERT ON `payments` FOR EACH ROW BEGIN
    DECLARE total_paid DECIMAL(10,2);
    DECLARE total_fee DECIMAL(10,2);

    -- Vérifier si l'étudiant existe avant d'effectuer les calculs
    IF EXISTS (SELECT 1 FROM `students` WHERE `id` = NEW.`student_id`) THEN
        -- Récupérer le total des paiements pour l'étudiant
        SELECT SUM(`amount`) 
        INTO total_paid
        FROM `payments` 
        WHERE `student_id` = NEW.`student_id`;

        -- Gérer les cas où aucun paiement précédent n'existe (éviter NULL)
        IF total_paid IS NULL THEN
            SET total_paid = 0;
        END IF;

        -- Récupérer la pension totale à payer pour l'étudiant
        SELECT c.`total_fee`
        INTO total_fee
        FROM `classes` c
        JOIN `students` s ON s.`class_id` = c.`id`
        WHERE s.`id` = NEW.`student_id`;

        -- Gérer les cas où la pension n'est pas trouvée
        IF total_fee IS NULL THEN
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Erreur: La pension totale pour la classe de l\'étudiant n\'a pas été trouvée.';
        ELSE
            -- Mettre à jour le reste à payer dans la table `students`
            UPDATE `students`
            SET `remaining_fee` = total_fee - total_paid
            WHERE `id` = NEW.`student_id`;
        END IF;
    ELSE
        -- Lever une erreur si l'étudiant n'existe pas
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Erreur: Étudiant non trouvé.';
    END IF;
END */;;
