CREATE  PROCEDURE `InsertUsers`(IN num_users INT)
BEGIN
    DECLARE i INT DEFAULT 1;
    DECLARE role_var ENUM('student', 'teacher', 'parent', 'admin');

    WHILE i <= num_users DO
        SET role_var = CASE
            WHEN i MOD 3 = 0 THEN 'teacher'
            WHEN i MOD 3 = 1 THEN 'student'
            ELSE 'parent'
        END;

        INSERT INTO users (first_name, last_name, email, phone, role, username, password)
        VALUES (CONCAT('User', i), CONCAT('LastName', i), CONCAT('user', i, '@example.com'), CONCAT('123456789', i), role_var, CONCAT('user', i), 'password');

        SET i = i + 1;
    END WHILE;
END ;;
